<?php

namespace Docova\DocovaBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * AppCssRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AppCssRepository extends EntityRepository
{
	/**
	 * Get all application Css data array/xml for view
	 *
	 * @param string $app_id
	 * @param string $format
	 * @return array
	 */
	public function getViewData($app_id, $format = 'array')
	{
		$output = array('@timestamp' => time(), '@toplevelentries' => '0');
		$query = $this->createQueryBuilder('C')
			->join('C.application', 'A')
			->where('C.application = :app')
			->andWhere('A.Trash = false')
			->andWhere('A.isApp = true')
			->addOrderBy('C.cssName', 'ASC')
			->setParameter('app', $app_id)
			->getQuery();
	
		$result = $query->getResult();
	
		if (!empty($result))
		{
			if ($format === 'array')
			{
				$output['@toplevelentries'] = count($result);
				$output['viewentry'] = array();
				foreach ($result as $css) {
					$output['viewentry'][] = array(
						'@unid' => $css->getId(),
						'entrydata' => array(
							array('text' => array($css->getCssName())),
							array('datetime' => array('@dst'=>true, 0 => $css->getDateModified()->format('Ymd his'))),
							array('text' => array($css->getModifiedBy()->getUserNameDnAbbreviated())),
							array('text' => array($css->getPDU() ? 'Yes' : 'No'))
						)
					);
				}
			}
			else {
				$xml = '<viewentries timestamp="'.time().'" toplevelentries="'.count($result).'">';
				foreach ($result as $css) {
					$xml .= "<viewentry unid='{$css->getId()}'>";
					$xml .= "<entrydata columnnumber='0'><text>{$css->getCssName()}</text></entrydata>";
					$xml .= "<entrydata columnnumber='1'><datetime dst='true'>{$css->getDateModified()->format('Ymd his')}</datetime></entrydata>";
					$xml .= "<entrydata columnnumber='2'><text>{$css->getModifiedBy()->getUserNameDnAbbreviated()}</text></entrydata>";
					$xml .= '<entrydata columnnumber="3"><text>'.($css->getPDU() ? 'Yes' : 'No').'</text></entrydata>';
					$xml .= '</viewentry>';
				}
				$xml .= '</viewentries>';
				$output = $xml;
			}
			$result = $css = null;
		}
		elseif ($format !== 'array') {
			$output = '<viewentries timestamp="'.time().'" toplevelentries="0"></viewentries>';
		}
		return $output;
	}
}
